<script setup lang="ts">
import WelcomeImage from "~/components/WelcomeImage.vue";
import {useFetch} from "#app";

definePageMeta({
  alias: ["/"],
  middleware: ["auth"],
});

let recentArticles = useState()
let topArticles = useState()

let fetchArticles = async () => {
  let res = await useAsyncData(async () => {
    const [recent, top] = await Promise.all([
      $fetch('/api/getRecentArticles'),
      $fetch('/api/getTopArticles')
    ])

    return { recent, top }
  })
  // let resTop = await useAsyncData(() => $fetch('/api/getTopArticles'))

  recentArticles.value = res.data.value.recent.items
  topArticles.value = res.data.value.top.items
  // topArticles.value = resTop.data.value.items
}

await fetchArticles()

let pb = await useNuxtApp().$pb;
const getImageUrl = (record: any) => {
  let url = pb.files.getUrl(record, record.image);
  return url
}
</script>

<template>
  <WelcomeImage/>

  <TabView>
    <TabPanel header="Top Articles">
      <ArticleCard
          class="mb-4"
          v-for="i in topArticles"
          :id="i.id"
          :image="`url('${getImageUrl(i)}')`"
          :title="i.title"
          :annotation="i.annotation"
          :author="i.author"
          image-size="20rem"
          height="10rem"
      />
    </TabPanel>
    <TabPanel header="Recent Articles">
      <ArticleCard
          class="mb-4"
          v-for="i in recentArticles"
          :id="i.id"
          :image="`url('${getImageUrl(i)}')`"
          :title="i.title"
          :annotation="i.annotation"
          :author="i.author"
          image-size="20rem"
          height="10rem"
      />
    </TabPanel>
  </TabView>

